   * @param  {Number} num
   * @return {String|Number}
   */
  function leftPadding(num) {
    if (num 
          ${now.time}
          ${now.date + ' ' + now.weekDesc}

      `;
    }

    display();
    container.appendChild($div);

    setInterval(function() {
      display();
    }, 500);
    console.log('时钟已创建');
  }

  return Clock;
